	my $num_N = $seq =~ s/N//g;
	my $num_gap = $seq =~ s/-//g;
	
	my $defined_length = $last_seq_length - $num_N - $num_gap;
	
	$header_to_def_len{$curr_header} = $defined_length;
	
	#print "seq $header has a defined length of $defined_length\n";
}


